/*===========================================*/
/*================== 提问页面 ==================*/
/*===========================================*/

.modal-dialog-title {
	background-color: #1575d5;
	background-image: none;
	padding: 10px 15px;
	border-radius: 6px 6px 0 0;
	border: 0px solid #0D6EB8;
	box-shadow: none;
	margin: 0px;
}

.modal-dialog-title-text {
	font-size: 14px;
	font-weight: 600;
	color: #fff;
	text-shadow: none;
}

.modal-dialog {
	border: 0px solid #888;
	box-shadow: 0 0 4px 0 rgba(0, 0, 0, .4);
}

.zg-form-text-input {
	box-shadow: none;
	border-radius: 3px;
	border: 1px solid #ddd;
	color: #999;
}

textarea.zg-form-text-input, .zg-form-text-input>textarea {
	color: #444;
}

.zm-editable-editor-field-wrap {
	border: 1px solid #ddd;
	box-shadow: none;
}

.zm-add-question-form-topic-wrap .zm-tag-editor-editor {
	box-shadow: none;
	border: 1px solid #ddd!important;
	padding: 8px 8px 3px;
	position: relative;
}

.zh-add-question-form .zm-add-question-form-sns-wrap .goog-checkbox.goog-checkbox-checked {
	box-shadow: none;
}

.zm-item-tag, .zm-tag-editor-edit-item {
	background: #E4EAFF;
	color: #698ebf;
	transition: all .1s;
}

html.no-touch .zm-item-tag:hover, html.no-touch .zm-item-tag-x:hover {
	background: #698ebf;
	color: #fff;
	text-decoration: none;
}

.zg-icon-dropdown-menu {
	width: 30px;
}
/*===========================================*/
/*================== 问题页面 ==================*/
/*===========================================*/

.zu-edit-button {
	transition: all .1s;
}

.question-invite-panel {
	margin: 15px 0 35px;
	color: #444;
	border: 0px solid #ddd;
	border-bottom: 1px solid #ddd;
	border-top: 1px solid #ddd;
	box-shadow: none;
	border-radius: 0px;
}

.question-invite-panel .search-input {
	color: #666;
	background: #F9F9FF;
	border: 1px solid #ddddFF;
}

.zg-form-text-input:focus {
	box-shadow: none;
	border: 1px solid #E2E0FF;
	position: relative;
	background: #FCFCFF;
}

.goog-toolbar {
	border-top: 1px solid #ddd;
	border-left: 1px solid #ddd;
	border-right: 1px solid #ddd;
	box-shadow: none;
	background-color: #f3f3f3;
	background-image: none;
}

.goog-toolbar-button, .goog-toolbar-menu-button:hover {
	box-shadow: none;
}

.zu-global-notify {
	top: 0px;
	box-shadow: none;
}

.zm-item-answer-author-wrap {
	margin: 1px 0 0 0;
}

.zm-item-vote-info {
	margin: 7px 0;
}

.zh-answer-form {
	margin-bottom: 15px;
	margin-top: 15px;
}

.goog-menu {
	background: #fff;
	color: #666;
	border: solid 1px #eee;
	box-shadow: none;
}

.zm-tag-editor-remove-button {
	transition: none;
}
/*===========================================*/
/*================== 话题页面 ==================*/
/*===========================================*/

.side-topic-content.ellipsis {
	left: 0px;
}

.pin-topic-avatar-link {
	position: relative;
	top: 5px;
}

.side-topic-item {
	padding: 13px;
}

.zm-topic-cat-sub p {
	height: 3.4em;
}

/*===========================================*/
/*================ 知乎专栏 ==================*/
/*===========================================*/

html body, html input, html textarea, html select, html button {
	font-family: '.SFNSText-Regular', 'Helvetica Neue', Helvetica, Arial, "Hiragino Sans GB", "Microsoft YaHei", "WenQuanYi Micro Hei", sans-serif;
	letter-spacing: 0.02em;
	text-rendering: optimizeLegibility;
}

body, input, textarea, select, button {
	font-size: 16px;
	line-height: 1.9;
	color: #444;
}

a {
	color: #259;
	text-decoration: none!important;
	-webkit-transition: all .2s ease-in-out;
	-moz-transition: all .2s ease-in-out;
	-ms-transition: all .2s ease-in-out;
	-o-transition: all .2s ease-in-out;
	transition: all .2s ease-in-out;
}

i, em {
	font-style: normal!important;
}

strong, b {
	font-weight: 600;
	letter-spacing: 0.02em;
	font-family: '.SFNSDisplay-Regular', 'Helvetica Neue', Helvetica, Arial, "Hiragino Sans GB", "Microsoft YaHei", "WenQuanYi Micro Hei", sans-serif;
}

h1, h2, h3, h4, h5 {
	font-weight: 600;
	letter-spacing: 0.02em;
	font-family: '.SFNSDisplay-Regular', 'Helvetica Neue', Helvetica, Arial, "Hiragino Sans GB", "Microsoft YaHei", "WenQuanYi Micro Hei", sans-serif;
}

.entry-content {
	line-height: 2;
}

.review-comment-panel.fold {
	height: 60px;
}

.pop-panel-title {
	padding: 10px 0px 0px 18px;
}

.home .card-item, .home .column-card-item, .home .post-card-item {
	height: 279px;
}

.review-comment-panel .toggle, .review-comment-panel .close {
	top: 11px;
}
/*===========================================*/
/*================== 宽屏适配 ==================*/
/*===========================================*/

body, input, textarea, select, button {
	font-size: 18px;
	line-height: 2;
	color: #444;
}

.receptacle {
	width: 1080px;
}

.post-write .title {
	font-size: 32px!important;
}

.post-write .toolbar-holder {
	width: 1080px;
}

.post-write .entry-content {
	min-width: 1080px;
	margin-left: -540px;
}

.navbar-title-container {
	width: 1080px;
	margin-left: -540px;
}

.post-view .entry-title {
	font-size: 40px;
	line-height: 60px;
}

@media (max-width: 1400px) and (min-width: 968px) {
	.receptacle {
		width: 660px!important;
	}
	.post-write .toolbar-holder {
		width: 660px!important;
	}
	.post-write .entry-content {
		min-width: 660px!important;
		margin-left: -330px!important;
	}
	.navbar-title-container {
		width: 660px!important;
		margin-left: -330px!important;
	}
	body, input, textarea, select, button {
		font-size: 15px!important;
	}
	.post-view .entry-title {
		font-size: 32px!important;
		line-height: 45px!important;
	}
}

.Card {
	border-radius: 6px;
}

.UserCover-image, .UserCover-image img {
	opacity: 0.2;
	-webkit-transition: -webkit-opacity 0.5s ease-out;
	transition: -webkit-opacity 0.5s ease-out;
	transition: opacity 0.5s ease-out;
	transition: opacity 0.5s ease-out, -webkit-transform 6s ease-out;
	border-top-right-radius: 5px;
	border-top-left-radius: 5px;
}

.UserCover:hover .UserCover-image {
	opacity: 1;
}

.UserCover {
	height: 100px;
	border-top-right-radius: 5px;
	border-top-left-radius: 5px;
}

.UserCoverGuide-item {
	border-top-right-radius: 5px;
	border-top-left-radius: 5px;
}

.UserCoverGuide-dialog {
